    /**
     * Run the database seeds.
     *
     * @return void
     */
    public function run()
    {
        foreach($this->blogs as $blog) {
            DB::table('blogs')->insert([
                'category_id'   => $blog['category_id'],
                'slug'          => str_slug($blog['title'], '-'),
                'title'         => $blog['title'],
                'editor'        => $blog['editor'],
                'images_path'   => $blog['images_path'],
                'description'   => $blog['description'],
                'created_at'    => Carbon::now()
            ]);
        }
    }
}
